using System.Collections.Generic;
using Apache.Ignite.Core;

var ignite = Ignition.Start();
var cache = ignite.GetOrCreateCache<int, Entity[]>("c");

cache.Put(1, new[]
{
    new Entity {Inner = new List<object>()},
    new Entity {Inner = new List<object>()}
});

cache.Get(1);

class Entity
{
    public IList<object> Inner { get; set; }
}
